import React, { useRef, useEffect } from "react";
import Avocata from './Avocata';
import mavatar from '../../media/avatar-m.png';
import favatar from '../../media/avatar-f.png';
const TagInput = React.lazy(()=> import('../SubComponents/TagInput'));

class AvocatasArea extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            avocatas: [],
            savedTags: ["DRWAING", "SINGING", "POETRY"],
            addedTags: []
        };
        this.user = this.props.user
        this.fileInput = React.createRef();
        this.saveTag = this.saveTag.bind(this);
        this.setAddedTags = this.setAddedTags.bind(this);
    }
    componentDidMount(){
        console.log('posts area mounted');
        console.log(this.fileInput.current)//.addEventListener('change', ()=>console.log('hello'));
    }
    saveTag(tagTxt){
        if (this.state.savedTags.indexOf(tagTxt) === -1) this.setState({savedTags: [...this.state.savedTags, tagTxt]});
    }
    setAddedTags(arr){
        this.setState({addedTags: arr});
    }
    render(){
        return (
            <section id="feed">
                {
                    //this.user &&
                    <form>
                        <div className="header">
                            <img alt="avatar" src={this.user.gender === 'male'? mavatar: favatar}/>
                            <p>{this.user.name}</p>
                        </div>
    
                        <textarea placeholder="content/caption" required/>

                        <React.Suspense fallback={<div>loading...</div>}>
                            <TagInput 
                                savedTags={this.state.savedTags} 
                                saveTag={this.saveTag} 
                                addedTags={this.state.addedTags} 
                                setAddedTags={this.setAddedTags}/>
                        </React.Suspense>

                        <label id="upload"> Upload image/mp3
                            <input type="file" accept="image/*,audio/mpeg" required ref={this.fileInput} />
                        </label>
                        <button>Publish</button>
                    </form>
                }
                {this.state.avocatas.map(console.log)}
                <Avocata />
                <Avocata />
            </section>
        );
    }
}

export default AvocatasArea;
