import {Link} from 'react-router-dom';
import logo from '../media/avocado_icon.png';
import favatar from '../media/avatar-f.png';
import mavatar from '../media/avatar-m.png';
import { useState } from 'react';

const AvocadoHeader =({user, setUser})=>{
    const [showOptions, setShowOptions] = useState(false);
    const [wannaDelete, setWannaDelete] = useState(false)
    
    const logOut =()=>{
        fetch('/user', {
            method: "PUT",
            headers: {"Content-Type": 'application/json'},
            body: JSON.stringify(user),
        })
        .catch(console.log);

        user.logged = false;
        setUser(false);
        setShowOptions(false);
    }

    const showDeletePrompt =()=>{
        setShowOptions(false);
        setWannaDelete(true);
    }

    const deleteAccount =(ev)=>{
        let passwordField = ev.target.previousElementSibling;
        if (passwordField.value !== user.password) {
            alert('password is not correct');
            return passwordField.value = '';
        }
        fetch('/user', {
            method: "DELETE",
            headers: {"Content-type": "application/json"},
            body: JSON.stringify(user),
        })
    }
    return (
        <header>
            <Link to="/" ><img alt="logo" src={logo}/></Link>
            <h1>Avocation|</h1>
            <p>your bright side hobbies</p>
            <div className='user'>
                {
                !user? 
                <Link to="/log-in" >Log In</Link>:
                <div className="userAvatar">
                    <img 
                        alt="avatar" 
                        src={user.gender === 'male'? mavatar:favatar}
                        onClick={()=>setShowOptions(!showOptions)}
                    />
                    
                    <div className={(showOptions? 'show ':'') + 'userOptions'}>
                        <h3>@{user.userName}</h3>
                        <br />
                        <Link to="/" onClick={logOut}>Log Out</Link>
                        <br />
                        <p onClick={showDeletePrompt} style={{color: 'red'}}>
                            Delete Account
                        </p>
                    </div>
                </div>
                }
            </div>
            {wannaDelete && 
                <div id="deletePrompt">
                    <p>sorry to see you leaving T-T</p>
                    
                    <input type="password" placeholder="Enter your password to confirm"/> <br />
                    <button onClick={deleteAccount}>Delete</button>
                    
                </div>
            }
        </header>
    );
}

export default AvocadoHeader;
