import React from "react";
import Avocata from './Avocata';
import AvocataForm from "../SubComponents/AvocataForm";


class AvocatasArea extends React.Component {
    constructor(props){
        super(props);
        this.state = {avocatas: [], };

        this.updataAvocatas = this.updataAvocatas.bind(this);
    }

    componentDidMount(){
        console.log(this.state.avocatas);
        console.log('posts area mounted');
        fetch('/all-avocatas')
        .then(resp=> resp.json())
        .then(avocatas=> {
            this.updataAvocatas(avocatas);      
        })
        .catch(console.log);
    }

    updataAvocatas(arr){this.setState({avocatas: arr})}

    render(){
        
        return (
            <section id="feed">
                {this.props.user && <AvocataForm user={this.props.user} avocatas={this.state.avocatas} updataAvocatas={this.updataAvocatas}/>}

                {this.state.avocatas.map(avocata=> <Avocata data={avocata} key={avocata.createdAt} users={this.props.users}/>)}

            </section>
        );
    }
}

export default AvocatasArea;
